//## Guild Application system
//################################################################################################################################
function makeAGuildApplication(){
	var status=GM_getValue('GuildApplication_'+GCAO_lang+'_'+GCAO_server, 'false');
	var time=day.getTime();
	if(status.match(/\d+/i)){
		if((time-status*1)>(1000*60*60*24)){
			GM_setValue('GuildApplication_'+GCAO_lang+'_'+GCAO_server, 'false');
		}
	}
	
	var div = document.createElement('div');
	div.setAttribute('class','contentItem');
	div.setAttribute('style','margin-top: 20px;');
	
	if(document.location.href.match('&save_application')){
		var insideString='<p>Your server guild application was applied.<br>Now every guild administrator in your server can browse your application.</p>';
	}else if(document.location.href.match('&withdraw_application')){
		var insideString='<p>Your server guild application was withdrawn.</p>';
	}else{
		if(getMyLevel()<15){
			var insideString='<p>You must be at least a 15 level player to make a server guild application.</p>';
			document.getElementById('content').appendChild(div);
		}else if(status=='false'){
			var insideString='<p>By making a server guild application, every server guild administrator will be able to see your request to find a guild.<br><br>Write about you in the following box:<br><textarea id="Server_Application_Text" style="height:80px;width:500px;"></textarea><br><i style="float:'+GCAO_right+';">(Your application will expire in 7 days)</i><br><input id="Server_Application_Button" type="button" class="button1" value="Apply"></p>';
			document.getElementById('content').appendChild(div);
		}else if(status=='true'){
			var insideString='<p>You have already make a server guild application.<br>Do you want to withdraw it?<br><i style="float:'+GCAO_right+';">(You will be able to make a new one after 24hours)</i><br><input id="Server_Application_Button" type="button" class="button1" value="Withdraw Application"></p>';
		}else{
			var time_dif=parseInt((1000*60*60*24)-(time-status*1)/1000);
			var total_time=secondsToTime(time_dif);
			var insideString='<p>You will be able to make a new server guild application in:<br>'+total_time.h+'h '+total_time.m+'m '+total_time.s+'s</p>';
		}
	}
	div.innerHTML='<h3>Server guild applications</h3><div class="contentItem_content">'+insideString+'</div>';
	document.getElementById('content').appendChild(div);
	if(document.getElementById("Server_Application_Button")){
		document.getElementById("Server_Application_Button").addEventListener('click',  function(){
			if(document.getElementById("Server_Application_Text")){
				GM_setValue('GuildApplication_'+GCAO_lang+'_'+GCAO_server, 'true');
				document.location.href+='&save_application';
			}else{
				var time=day.getTime();
				time=time+'';
				GM_setValue('GuildApplication_'+GCAO_lang+'_'+GCAO_server, time);
				document.location.href+='&withdraw_application';
			}
		}, false);
	}
}

