function basikes(){
    var tot = 0;
    if(document.getElementById('char_f0_tt')){
		var stats = new Array(2);
		for (x=0;x<=5;x++){
			stats[x,0] = document.getElementById('char_f'+x+'_tt').getAttribute('onmouseover');
			stats[x,1] = stats[x,0].match(/\\'>(\d+)/g)[2].match(/(\d+)/g)[0];
			if(stats[x,0].match(/\\'>\+(\d+)/g)){
				stats[x,3] = stats[x,0].match(/\+(\d+)/g)[0];
				stats[x,4] = stats[x,0].match(/\+(\d+)/g)[1];
				stats[x,3] = stats[x,4]*1-stats[x,3]*1;
			}else if(stats[x,0].match(/\\'>-(\d+)/g)){
				stats[x,3] = stats[x,0].match(/-(\d+)/g)[0];
			}else{
				stats[x,3] = 0;
			}
			stats[x,0] = stats[x,0].match(/\\'>(\d+)/g)[1].match(/(\d+)/g)[0];
			stats[x,2] = nextPoints(stats[x,0]);
			
			//Old type of displaying base points etc (before Gladiatus v1.5.0)
			//document.getElementById('char_f'+x).setAttribute('style','color: #540400;display: block;float: '+GCAO_right+';font-weight: bold;position: relative;'+GCAO_right+': 5px;text-align: '+GCAO_right+';top: -15px;width: 130px;');
			//document.getElementById('char_f'+x).innerHTML += ' | <font color="#505050">' + stats[x,0] + '</font> | <font color="red">' + stats[x,3] + '</font> | <font color="green">+' + stats[x,2] + '</font>';
			document.getElementById('char_f'+x).setAttribute('style','width: 265px;'+GCAO_right+':125px;');
			document.getElementById('char_f'+x).getElementsByTagName('div')[6].setAttribute('style','width:65px;color:#540400;');
			document.getElementById('char_f'+x).getElementsByTagName('div')[6].innerHTML+='<font style="font-size:0.7em;color:#505050;"> / '+stats[x,1]+'</font>';
			document.getElementById('char_f'+x).innerHTML +='<div class="training_operator">'+training_symbol+'</div> <div class="training_value" style="color:green;">+' + stats[x,2] + '</div>';
			
			if(x==0){tot += statCost(5, stats[x,0], 2.6, 0);}
			else if((x==1)||(x==4)){tot += statCost(5, stats[x,0], 2.5, 0);}
			else if((x==2)||(x==3)){tot += statCost(5, stats[x,0], 2.3, 0);}
			else if(x==5){tot += statCost(5, stats[x,0], 2.4, 0);}
		}
		
		var div = document.createElement('font');
		div.setAttribute('style','font-weight:normal;position:absolute;margin-top:-235px;margin-'+GCAO_left+':30px;')
		div.innerHTML=dottedNumber(tot)+' <img border="0" align="absmiddle" src="'+img_gold+'"> '+L_spent;
		document.getElementById('content').appendChild(div);
	}
}
     
// return the cost of a stat from begin to end.
/**
coeff : 2.6 <-> Strength
2.5 <-> skill/charisma
2.4 <-> intelligence
2.3 <-> agility/constitution
**/
function statCost(begin, end, coeff, reduc){
    var count = 0;
    begin = parseInt(begin);
    end = parseInt(end);
     
    for(var i = begin; i < end; i++)
    count += parseInt(Math.pow(i-4,coeff)*(1-reduc));
     
    return count;
}
     
//How much the max will increase / Πόσο θα αυξηθεί το μέγιστο
function nextPoints(stat){
    stat = stat*1;
    if(stat%2==1){var nextPoints=2;}
    else{var nextPoints=1;}
    return nextPoints;
}
     
