function getTheItemType(itemImage){
	itemImage=itemImage.match(/(\d+)_/i)[1]*1;
	//alert(itemImage);
	var itemType=itemImage;
	switch(itemType){
		//0.Helmet,1.Sword,2.Shield,3.Armor,4.Ring,5.Ring,6.Gloves,7.Boots,8.Amulet
		//case 0: itemType=9;break;
		case 1: itemType=1;break;
		case 2: itemType=2;break;
		case 3: itemType=3;break;
		case 4: itemType=0;break;
		case 5: itemType=6;break;
		case 6: itemType=4;break;//Rings
		case 8: itemType=7;break;
		case 9: itemType=8;break;
		case 15: itemType=11;break;//Mercenaries
		default: itemType=10;break;//Not compared items
	}
	return itemType;
}

