function searchItems(){
	if(!document.getElementById('Results')){
		var newElement = document.createElement('div');
		newElement.setAttribute('style', 'margin-top:50px;border:1px solid #af8e50;background-color:#ded8c6;padding:5px;');
		newElement.innerHTML = '<table id="Results"><tr><b>'+L_Results+':</b></tr></table><br/>'+
							'<table id="FoundItems"><tr><b>'+L_itemsFound+':</b></tr></table>';
		document.getElementById('content').appendChild(newElement);
	}else{
		document.getElementById('Results').innerHTML='';
		document.getElementById('FoundItems').innerHTML='';
	}
	
	var itemName=doc.getElementById('searchInput').value.toLowerCase();
	if(itemName!='' && itemName!=' ' && !itemName.match(/[<>=+-]/i)){
		for(y=1;y<=5;y++){
			for(c=0;c<=1;c++){
				GM_xmlhttpRequest({
					method: "GET",
					url: GCAO_siteurl+'mod=inventory&subsub='+c+'&sub='+y+'&sh='+GCAO_secureCode,
					onload: function(response){
						if(response.responseText.match(/var INIT_DHTML = true;/i)){
							var dealerName=response.responseText.match(/class="pngfix  current" style="cursor:pointer;">([^<]+)<\/a>/i)[1];
							var data=response.responseText.split('<script');
							var x=0;
							while(!data[x].match(/var INIT_DHTML = true;/i)){
								x++;
							}
							data=data[x];
							
							var dealerNum=data.match(/aktShopPage = (\d+);/i)[1];
							var items=data.split('AddCharDiv(\'p'+dealerNum);
							
							x=0;
							var item_number=0;
							var the_items='';
							while(items[x]){
								if(items[x].toLowerCase().match(itemName)){
									var temp=items[x].match(/<table.+table>/i)+'';
									the_items+=temp.replace(/\\'/g,"'");
									item_number++;
								}
								x++;
							}
							
							if(item_number>0){
								var plus='<a style="text-decoration:none;" onclick="if(document.getElementById(\'Items_'+dealerNum+'\').getAttribute(\'style\')==\'display:none;\'){document.getElementById(\'Items_'+dealerNum+'\').setAttribute(\'style\',\'display:block;\');this.innerHTML=\'(-)\';}else{document.getElementById(\'Items_'+dealerNum+'\').setAttribute(\'style\',\'display:none;\');this.innerHTML=\'(+)\';}">(+)</a>';
							}else{
								var plus='';
							}
							
							var tab=parseInt(response.responseText.match(/<form action="index.php.mod=inventory&&sub=\d+&subsub=(\d+)/i)[1])+1;
							var newElement = document.createElement('tr');
							newElement.innerHTML = '<td>'+dealerName+' ('+tab+'):</td><td>'+item_number+'</td><td>'+plus+'</td>';
							document.getElementById('Results').appendChild(newElement);
							
							if(item_number>0){
								var newElement = document.createElement('tr');
								newElement.setAttribute('id','Items_'+dealerNum);
								newElement.setAttribute('style','display:none;');
								newElement.innerHTML = '<td>'+the_items+'</td>';
								document.getElementById('FoundItems').appendChild(newElement);
							}
						
						}else{
							var newElement = document.createElement('tr');
							newElement.innerHTML = '<td colspan="3">Getting data error</td>';
							document.getElementById('Results').appendChild(newElement);
						}
					}
				});
			}
		}
	}
}

