function calculateForGuildUpgrade2(){
	var targetGold=parseInt(document.getElementById('targetGold').innerHTML.replace(/\./gi,''));
	var Gold=targetGold;
	var totalLevel=0;
	var trs=document.getElementById('mainbox').getElementsByTagName('tr').length;
	for (var i=1;i<=(trs-3);i++){
		if(document.getElementById('pls_ckeck_'+i).checked==true){
			if(document.getElementById('pls_input_'+i).value==0 || document.getElementById('pls_input_'+i).value==''){
				var level=parseInt(document.getElementById('mainbox').getElementsByTagName('tr')[i].getElementsByTagName('td')[2].innerHTML);
				totalLevel+=level;
			}else{
				var fixedGold=parseInt(document.getElementById('pls_input_'+i).value);
				Gold-=fixedGold;
			}
		}
	}
	
	var futureGatheredGold=0;
	if(document.getElementById('ckeck_calculate_bank').checked==true){
		Gold-=parseInt(document.getElementById('bank_gold').innerHTML.replace(/\./gi,''));
		futureGatheredGold+=parseInt(document.getElementById('bank_gold').innerHTML.replace(/\./gi,''));
	}
	
	if(Gold<0){Gold=0}
	var goldPerLevel=Gold/totalLevel;
	for (var i=1;i<=(trs-3);i++){
		if(document.getElementById('pls_ckeck_'+i).checked==true){
			if(document.getElementById('pls_input_'+i).value==0 || document.getElementById('pls_input_'+i).value==''){
				var level=parseInt(document.getElementById('mainbox').getElementsByTagName('tr')[i].getElementsByTagName('td')[2].innerHTML);
				var playerGold=parseInt(level*goldPerLevel)+1;
			}else{
				var fixedGold=parseInt(document.getElementById('pls_input_'+i).value);
				var playerGold=fixedGold;
			}
			futureGatheredGold+=playerGold;
			document.getElementById('mainbox').getElementsByTagName('tr')[i].getElementsByTagName('td')[4].innerHTML=dottedNumber(playerGold)+' <img align="absmiddle" border="0" src="'+img_gold+'">';
		}else{
			document.getElementById('pls_input_'+i).value=0;
			document.getElementById('mainbox').getElementsByTagName('tr')[i].getElementsByTagName('td')[4].innerHTML=' - ';
		}
	}
	var result=futureGatheredGold-targetGold;
	if(result<0){
		document.getElementById('result').innerHTML='<font color="red">'+dottedNumber(result)+'</font> <img align="absmiddle" border="0" src="'+img_gold+'">';
	}else{
		document.getElementById('result').innerHTML='<font color="green">+'+dottedNumber(result)+'</font> <img align="absmiddle" border="0" src="'+img_gold+'">';
	}
}

