//## TAB TITLE
//################################################################################################################################
function tabTitle(){
	if(!document.title.match(' Gladiatus')){
		var y=GM_getValue('tb', true);
		var z=GM_getValue('tc', true);
		var x=GM_getValue('ctt', 0);
		var k=GM_getValue('td', true);
		
		var text='';
		
		if(y){
			text+='Gladiatus';
			if(z){
				text+=' (s'+GCAO_server+'_'+GCAO_lang+')';
			}
			text+=': ';
		}
		
		if(x==0){
			var gold=document.getElementById('sstat_gold_val').innerHTML.replace(/\./gi,'')*1;
			text+=dottedNumber(gold)+' '+L_gold;
		}else if(x==1){
			var time=document.getElementById('cooldown_bar_text_expedition').innerHTML;
			text+=isItTime(time);
			if(k){
				var points=document.getElementById('expeditionpoints_value').innerHTML;
				text+=' ('+points.replace(/ /g,'')+')';
			}
			text+=' '+L_expedition;
		}else if(x==2){
			var time=document.getElementById('cooldown_bar_text_dungeon').innerHTML;
			text+=isItTime(time);
			if(k){
				var points=document.getElementById('dungeonpoints_value').innerHTML;
				text+=' ('+points.replace(/ /g,'')+')';
			}
			text+=' '+L_dungeon;
		}else if(x==3){
			var time=document.getElementById('cooldown_bar_text_arena').innerHTML;
			text+=isItTime(time)+' '+L_arena.toLowerCase();
		}else if(x==4){
			var time=document.getElementById('cooldown_bar_text_ct').innerHTML;
			text+=isItTime(time)+' '+L_turma;
		}
		
		if(!document.title.match(' 00:00 ')){
			document.title=text;
		}else{
			document.title=L_Done;
		}
		setTimeout(tabTitle, 1000);
	}
}

function isItTime(text){
	if(!text.match(/\d/) && !text.match('-')){
		text=' 00:00 ';
	}
	return text;
}

