//INSERT AFTER / ΒΑΛΕ ΜΕΤΑ ΑΠΟ
//Command / Εντολή: insertAfter('newElement','targetElement');
function insertAfter(newElement,targetElement) {
	var parent = targetElement.parentNode;
	if(parent.lastchild == targetElement) {
		parent.appendChild(newElement);
	}
	else {
		parent.insertBefore(newElement, targetElement.nextSibling);
	}
}

//Add A DOT "." FOR THOUSAND, MILLION, BILLION, etc...
function dottedNumber(nbr){
	x=false;
	if(nbr<0){var x=true;nbr=0-nbr;}
	var number = ''+nbr;
	var returned = '';
	while(number.length>3){
	returned= "."+number.substring(number.length-3,number.length)+returned;
	number = number.substring(0,number.length-3);
	}
	if(number.length>0)
	returned = number+returned;
	if(x){var returned='-'+returned}
	return returned;
}

//Round a number (ex.: 1,3899 -> 1,38)
function roundNumber(x){
	x = x*(100);
	x = (x-x%1)/(100);
	return x;
}

//Convert to Unicode
function toASCII(theString){
	var asciiString = '&#';
	for (i=0;i < theString.length; i++) {
		asciiString += theString.charCodeAt(i) + '&#';
	}
	asciiString = asciiString.substr(0, asciiString.length - 2);
	return asciiString;
}

//Decode ASCII
function decodeASCII(ASCII){//String.fromCharCode(
	var decodedString = '';
	ASCII=ASCII.split('&#');
	for (i=0;i < ASCII.length; i++) {
		decodedString += String.fromCharCode(ASCII[i]);
	}
	return decodedString;
}

function secondsToTime(secs){
    var hours = Math.floor(secs / (60 * 60));
   
    var divisor_for_minutes = secs % (60 * 60);
    var minutes = Math.floor(divisor_for_minutes / 60);
 
    var divisor_for_seconds = divisor_for_minutes % 60;
    var seconds = Math.ceil(divisor_for_seconds);
   
    var obj = {
        "h": hours,
        "m": minutes,
        "s": seconds
    };
    return obj;
}

