	p.snapshotItem(0).innerHTML += '<br/><br/><input id="launchRaidedGold" type="button" class="button2" value="'+L_findGold+'" style="'+GCAO_left+':500px;top:374px;"><input id="hasBeenLaunched" type="hidden" value="0">   ';
	
	// the bar
	var bar = document.createElement('div');
	if(!document.getElementById('banner_top')){var top = 401-25;}
	else{var top = 401;}
	bar.innerHTML = '<div class="cooldown_bar" style="'+GCAO_left+':630px;top:'+top+'px;cursor:pointer;"><div id="pageBar" style="width: 0%;" class="cooldown_bar_fill cooldown_bar_fill_progress"></div><div class="cooldown_bar_text" id="cooldown_bar_fill_page">0</div></div>';
